import { View, ScrollView, Text, StyleSheet, TouchableOpacity } from 'react-native'
import React, { useEffect, useState } from 'react'
import CardRoom from './CardRoom'


export default function SaveRoom({ navigation }) {


  const [arrData, SetArrData] = useState([])

  // useEffect(() => { FetchData(); }, []);

  const FetchData = async () => {
    const requestOptions = {
      method: 'GET',
      redirect: 'follow'
    };

    await fetch("http://localhost:49674/api/Rooms", requestOptions)
      .then(response => response.text())
      .then(result => SetArrData(result.data))
      .catch(error => console.log('error', error));
  }


  // const FetchData = () => {
  //   return fetch('http://localhost:49674/api/Rooms', {method: 'POST'})
  //     .then((response) => response.json())
  //     .then((json) => {
  //       SetArrData(json)
  //     })
  //     .catch((error) => {
  //       console.error(error);
  //     });
  // };


  // console.log("arrData: " + arrData);

  return (
    <ScrollView>
      <Text style={styles.HeadLine}>Choose a room</Text>
      <CardRoom />
      <CardRoom />
      <CardRoom />
      <View style={styles.save}>
        <TouchableOpacity style={styles.button} onPress={() => navigation.navigate('Payment')} >
          <Text>Save</Text>
        </TouchableOpacity>
      </View>
    </ScrollView>
  )
}

const styles = StyleSheet.create({
  HeadLine: {
    fontSize: 30,
    fontWeight: "bold",
    paddingTop: 20,
    paddingBottom: 20,
    alignItems: "center",
    textAlign: "center",
    justifyContent: "center",
  },
  save: {
    paddingBottom: 30,
    paddingTop: 20,
    width: 100,
    alignSelf: 'center',
  },
  button:
  {
    flex: 1,
    width: 100,
    backgroundColor: 'gray',
    padding: 10,
    borderRadius: 10,
    alignItems: 'center'
  },
})
